# Define o estado inicial da soundtrack do jogo
OST.SETUP:		la 		t0, HOLLOW				# Carrega o endereço da musica
			la 		t1, OST_STATUS				# Carrega o endereço do buffer do estado da musica
			sw 		t0, 4(t1)				# Guarda na primeira word do estado o endereço da musica
			sw 		zero,0(t1)				# Guarda zero no segundo estado (servirá para calcular o tempo da nota)
			
			la 		t0, HOLLOW_2				# Carrega o endereço da musica
			la 		t1, OST_STATUS_2			# Carrega o endereço do buffer do estado da musica
			sw 		t0, 4(t1)				# Guarda na primeira word do estado o endereço da musica
			sw 		zero,0(t1)				# Guarda zero no segundo estado (servirá para calcular o tempo da nota)
			
			ret

# Toca uma nota	
OST.TOCA:		la 		a0, OST_STATUS				# Endereçom do estado
			li 		a2, 0					# Instrumento (Piano)
			li 		a3, 30					# Volume
			
			lw		t0, 0(a0)				# Carrega para t0 o tempo da próxima nota
			beqz 		t0, OST.TOCA.NOTA			# Se for = 0, toca a nota
	
			csrr 		t1, 3073				# Carrega para t1 o tempo atual
			bltu		t1, t0, OST.RET				# Se o tempo atual for menor que o tempo da próxima nota, não toca

OST.TOCA.NOTA:		lw 		t0, 4(a0)				# Carrega para t0 o endereço da musica
			lw 		t1, 0(t0)				# Carrega para t1 a nota
			lw 		t2, 4(t0)				# Carrega para t2 a duração
	
			beqz 		t1, OST.TERMINOU			# Se a nota for = 0, terminou a música
	
			mv 		t3, a0					# Guarda em t3 o endereço de estado
	
			mv 		a0, t1					
			mv 		a1, t2
			li		a7, 31
			ecall							# Toca a nota
	
			mv 		a0, t3					# Retorna o endereço de estado para a0

OST.TERMINOU:		beqz 		t2, OST.SETUP				# Se a duração for = 0, recomeça a musica
	
			csrr 		t3, 3073				# Guarda em t3 o tempo atual
			add 		t3, t3, t2				# Acrescenta o tempo da nota
			sw 		t3, 0(a0)				# Guarda o tempo da nota no endereçom de estado
			
			addi 		t0, t0, 8				# Avança para a próxima nota
			sw 		t0, 4(a0)				# Guarda o endereço de estado atualizado
	
OST.RET:		ret							# Retorna ao loop principal

# Toca uma nota	
OST.TOCA_2:		la 		a0, OST_STATUS_2			# Endereçom do estado
			li 		a2, 0					# Instrumento (Piano)
			li 		a3, 30					# Volume
			
